import React from 'react';
import Head from 'next/head';
import Image from 'next/image';

import Section from '../components/Section';
import Project from '../components/Project';
// import Tooltip from '../components/Tooltip';
import IconContainer from '../components/Icons/IconContainer';
import Icon from '../components/Icons/Icon';


import { FaStripe } from "react-icons/fa"
import { IoLogoVimeo } from "react-icons/io";
import { IoLogoVue, IoLogoSass } from "react-icons/io5";
import { DiNodejs } from "react-icons/di";
import { SiBulma, SiFirebase, SiNetlify, SiMongodb, SiGooglecloud, SiVuetify, SiGithub, SiFacebook, SiReddit } from "react-icons/si";

import ChakraLogo from '../components/svg/chakra';
// import MDLogo from '../components/svg/material-design';



export default class Home extends React.Component {
  render() {
    return (
      <>
        <Head>
          <title>Spencer Speas</title>
        </Head>
        <div className="mx-auto">


          <div className="relative h-screen">
            <div className="bg-gray-800 h-full w-full flex flex-col items-center justify-center text-white">
              <h1 className="text-5xl font-extrabold">Spencer Speas</h1>
              <h2 className="text-2xl pt-2 font-mono font-bold">SaaS Specialist</h2>
            </div>
            <div className="absolute bottom-0 z-5 bg-white w-full">
              <img src="/images/profile.jpg" alt="My wedding day" 
                   className="rounded-full w-72 shadow-md transition duration-500 ease-in-out transform hover:scale-105 hover:shadow-lg transform -translate-x-1/2 left-1/2" 
              />
            </div>
          </div>


          <Section title="Polished Projects">
            <div className="flex flex-col items-center md:grid grid-cols-2 lg:grid-cols-3 container gap-4">


              {/* Aceso */}
              <Project
                title="Aceso Recovery"
                image={{
                  src: 'images/Aceso/Landing.jpg',
                }}
                logo={{
                  src: 'images/Aceso/Logo.png'
                }}
                link={{
                  url: "https://www.AcesoRecovery.com",
                  pretty: "AcesoRecovery.com"
                }}
                description="Aceso bridges the gap between physical therapists and their patients."
              >

                <IconContainer
                  top={<>
                    { Icon(IoLogoVue, { href: "https://google.com", color: "#42b983", label: "Vue" }) }
                    { Icon(SiBulma, { href: "https://google.com", color: "#00d1b2", label: "Bulma" }) }
                    { Icon(SiFirebase, { href: "https://firebase.google.com/", color: "gold", label: "Firebase" }) }
                    { Icon(SiNetlify, { href: "https://www.netlify.com/", color: "#15847D", label: "Netlify" }) }
                  </>}
                  bottom={<>
                    { Icon(IoLogoVimeo, { href: "https://vimeo.com/", color: "rgb(15, 174, 241)", label: "Vimeo" }) }
                    { Icon(FaStripe, { href: "https://stripe.com/", color: "white", label: "Stripe" }) }
                    { Icon(IoLogoSass, { href: "https://sass-lang.com/", color: "#bf4080", label: "Sass" }) }
                  </>}
                  showBoth={true}
                />
              </Project>



              {/* Repairman Pro */}
              <Project
                title="Repairman Pro"
                image={{
                  src: 'images/Repairman/Landing.jpg',
                }}
                logo={{
                  src: 'images/Repairman/Logo.png'
                }}
                link={{
                  url: "https://repairman-pro.app",
                  pretty: "Repairman-Pro.app"
                }}
                description="Automatic device inspection checklists, connected with RepairDesk."
              >
                <IconContainer
                  top={<>
                    { Icon(IoLogoVue, { href: "https://google.com", color: "#42b983", label: "Vue" }) }
                    { Icon(ChakraLogo, { href: "https://vue.chakra-ui.com/", label: "ChakraUI" }) }
                    { Icon(Image, { href: "https://www.repairdesk.co/", src: "/images/icons/repairdesk.svg", label: "RepairDesk" }) }
                  </>}
                  bottom={<>
                    { Icon(SiMongodb, { href: "https://google.com", color: "gold", label: "MongoDB" }) }
                    { Icon(DiNodejs, { href: "https://google.com", color: "white", label: "NodeJS" }) }
                  </>}
                  showBoth={true}
                />
              </Project>



              {/* Aceso ML */}
              <Project
                className="md:row-span-2"
                title="Aceso ML"
                image={{
                  src: 'images/Aceso/Landing.jpg',
                }}
                logo={{
                  src: 'images/Aceso/Logo.png'
                }}
                link={{
                  url: "https://www.AcesoRecovery.com",
                  pretty: "AcesoRecovery.com"
                }}
                description="Aceso bridges the gap between physical therapists and their patients."
              >
                <IconContainer
                  top={<>
                    { Icon(IoLogoVue, { href: "https://google.com", color: "#42b983", label: "Vue" }) }
                    { Icon(Image, { href: "https://vuetifyjs.com/en/", label: "Vuetify", src: "/images/icons/vuetify.svg" }) }
                    {/* { Icon(SiVuetify, { href: "https://vuetifyjs.com/en/", color: "cyan", label: "Netlify" }) } */}

                    { Icon(Image, { href: "https://firebase.google.com/", label: "Firebase", src: "/images/icons/firebase.png" }) }
                    { Icon(SiNetlify, { href: "https://www.netlify.com/", color: "#15847D", label: "Netlify" }) }
                  </>}
                  bottom={<>
                    { Icon(SiBulma, { href: "https://google.com", color: "#00d1b2", label: "Bulma" }) }
                    { Icon(Image, { href: "https://google.com", src: "/images/icons/google-cloud.png", label: "Google Compute Engine" }) }
                    {/* { Icon(SiGooglecloud, { href: "https://google.com", label: "Google Compute Engine" }) } */}
                  </>}
                  showBoth={true}
                />
              </Project>


            </div>
          </Section>

          {/* <Section title="In Progress">
            <p>Testing</p>
          </Section> */}


          <Section title="Contact Info">

            <div className="flex flex-col md:grid md:grid-cols-2 gap-4">
              <div className="flex flex-col">
                <p className="text-left md:text-right">
                  <a href="mailto:spencer.speas@gmail.com" className="cursor-pointer text-blue-400 hover:text-blue-500">
                    Spencer.Speas@Gmail.com
                  </a>
                  <br />
                  <a href="tel:+19169901341" className="cursor-pointer text-blue-400 hover:text-blue-500">
                    (916) 990-1341
                  </a>
                </p>
              </div>
              <div className="flex flex-col text-left">
                <div className="bg-gray-800 rounded-lg flex flex-row justify-between py-2 px-4">
                  <p>Github</p>
                  { Icon(SiGithub, { href: "https://github.com/spenco100", color: "white", label: "Spenco100" }) }
                </div>
                <div className="bg-gray-800 rounded-lg flex flex-row justify-between py-2 px-4">
                  <p>Reddit</p>
                  { Icon(SiReddit, { href: "https://reddit.com/u/sragan16", color: "white", label: "Spenco100" }) }
                </div>
                <div className="bg-gray-800 rounded-lg flex flex-row justify-between py-2 px-4">
                  <p>Facebook</p>
                  { Icon(SiFacebook, { href: "https://facebook.com/spenco100", color: "white", label: "Spenco100" }) }
                </div>
              </div>
            </div>
          </Section>


        </div>


      </>
    )
  }
}
